booksPage.getRowsCount().then(initialCount => {
  expect(initialCount, 'initial row count').to.be.greaterThan(0);
  // Filtrar primero para probar el "reset"
  booksPage.search(data.validSearchTerm);
  // En UI real puede que no reduzca si el término matchea todo,
  // pero en general debe ser <= initial.
  booksPage.getRowsCount().should('be.at.most', initialCount);
  // Vacío => vuelve baseline
  booksPage.clearSearch();
  booksPage.getRowsCount().should('equal', initialCount);
});

booksPage.getRowsCount().then(initialCount => {
  expect(initialCount, 'initial row count').to.be.greaterThan(0);
  // Requiere que exista en fixture
  expect(data.whitespaceSearchTerm, 'whitespaceSearchTerm in fixture').to.exist;
  booksPage.search(data.whitespaceSearchTerm);
  // Comportamiento aceptado:
  // A) whitespace tratado como empty => baseline
  // B) whitespace tratado como literal => 0
  booksPage.getRowsCount().then(countAfter => {
    const accepted = countAfter === initialCount || countAfter === 0;
    expect(accepted, `rows after whitespace should be ${initialCount} or 0`).to.eq(true);
  });
  // La UI debe seguir usable: limpiar vuelve baseline
  booksPage.clearSearch();
  booksPage.getRowsCount().should('equal', initialCount);
});
